mapper.select:
SELECT account.login_name AS loginName, account_role.account_id AS accountId, role.* FROM manager_role role LEFT JOIN manager_account_role account_role ON role.id = account_role.role_id LEFT JOIN manager_application app ON app.id = role.application_id LEFT JOIN manager_account account on account_role.account_id =account.id
where 1=0
or ( account_role.account_id in
#{accountId}
and app.application_code=#{applicationCode}
)
